INSERT INTO `modules` (`moduleID`, `moduleName`, `moduleDescription`) VALUES
(1, 'Administration', 'Ce module intègre toutes les données de bases nécessaires à l’administration du site et des données communes à l’application.\r\n- Gestions des paramètres globaux de l’application\r\n- Gestion des utilisateurs et des rôles\r\n- Gestion des classes\r\n- Gestion des droits d’accès aux modules\r\n'),
(2, 'Rédaction', 'Ce module permet de gérer les contenus des énoncés de TPIs, l’attribution des TPIs aux experts et la relation entre apprentis, experts et profs/chefs de projet. Les énoncés peuvent inclure des images'),
(3, 'Répartition', 'Ce module permet au collège d’experts de se répartir les TPIs en définissant les experts 1 et 2 de chaque TPI. \r\nDans la continuité d’une année scolaire, ce module s’intègre dans le module précédent (rédaction des énoncés).\r\nCe module n’est accessible qu’aux experts et à l’administrateur. '),
(4, 'Validation', 'Ce module sert à valider le contenu de l\'énoncé, et de s\'assurer que le cadre de travail correspond aux règlements.\r\nLors de l’entretien préliminaire, les experts, conjointement avec le prof / chef de projet, passent en revue une série de critères de validation.'),
(5, 'Evaluation', 'Ce module permet de suivre et évaluer l\'apprenti et son travail au cours de la période d’examen.\r\nLes experts peuvent intégrer leurs notes de passage, le prof/chef de projet peut remplir les parties de la feuille d’évaluation qui le concerne. \r\nLes experts peuvent ajouter les 6 questions préparées, l’évaluation des réponses ainsi qu’attribuer les points qui correspondent à leur partie.\r\n');
